dt-option.example:
$('#example').DataTable( {
	responsive: true,
	columnDefs: [
		{ responsivePriority: 1, targets: 0 },
		{ responsivePriority: 2, targets: -1 }
	]
} );

$('#example').DataTable( {
	responsive: true,
	columns: [
		{ responsivePriority: 6 },
		{ responsivePriority: 5 },
		{ responsivePriority: 4 },
		{ responsivePriority: 3 },
		{ responsivePriority: 2 },
		{ responsivePriority: 1 }
	]
} );
