dt-option.example:
new DataTable('#myTable', {
	responsive: {
		breakpoints: [
			{ name: 'desktop', width: Infinity },
			{ name: 'tablet', width: 1024 },
			{ name: 'fablet', width: 768 },
			{ name: 'phone', width: 480 }
		]
	}
});

DataTable.Responsive.breakpoints = [
	{ name: 'desktop', width: Infinity },
	{ name: 'tablet', width: 1024 },
	{ name: 'fablet', width: 768 },
	{ name: 'phone', width: 480 }
];

new DataTable('#myTable', {
	responsive: true
});
